#[cfg(test)]
mod test {
    use mina_hasher::{Fp, Hashable, ROInput};
    use mina_signer::{create_kimchi, PubKey, Signature, Signer};

    #[derive(Clone)]
    struct Msg {
        inner: Vec<Fp>,
    }

    impl Hashable for Msg {
        type D = ();
        fn to_roinput(&self) -> ROInput {
            let mut ro = ROInput::new();

            for field in self.inner.iter() {
                ro = ro.append_field(field.clone());
