        let pubkey =
            PubKey::from_address("B62qoTFrus93Ryi1VzbFakzErBBmcikHEq27vhMkU4FfjGfCovv41fb")
                .unwrap();

        let mut signer = create_kimchi::<Msg>(());

        let is_verified = signer.verify(
